@prefix rr: <http://www.w3.org/ns/r2rml#>.
@prefix rml: <http://semweb.mmlab.be/ns/rml#>.
@prefix ql: <http://semweb.mmlab.be/ns/ql#>.
@prefix transit: <http://vocab.org/transit/terms/>.
@prefix xsd: <http://www.w3.org/2001/XMLSchema#>.
@prefix foaf: <http://xmlns.com/foaf/0.1/>.
@base <http://example.com/ns#>.

<#TriplesMap1> rml:logicalSource [ 
     	  rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/people.csv";
     	  rml:referenceFormulation ql:CSV
	];
        rr:subjectMap [ 
     	  rr:termType rr:IRI;
          rr:template "http://sample.com/resource/P{ID}";
     	  rr:class foaf:Person
     	];
        rr:predicateObjectMap [ 
     	  rr:predicate  foaf:name;  
     	  rr:objectMap [ 
     	    rml:reference "Name"; rr:termType rr:Literal ;
     	    rr:datatype xsd:string
     	  ] 
  	];
 	rr:predicateObjectMap [  
     	  rr:objectMap [ 
     	    rr:termType rr:Literal ;
	  ] 
  	] .

