user_template:
@prefix rr: <http://www.w3.org/ns/r2rml#> .
@prefix rml: <http://semweb.mmlab.be/ns/rml#> .
@prefix ql: <http://semweb.mmlab.be/ns/ql#> .
@prefix transit: <http://vocab.org/transit/terms/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix sch: <http://schema.org/> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix onto: <http://vocab.es/schema#> .
@prefix geo: <http://www.w3.org/2003/01/geo/wgs84_pos#> .
@base <http://example.com/ns#> .

<#TriplesMap2> rml:logicalSource [
		rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/nested_locations1.xml" ;
		rml:referenceFormulation <http://semweb.mmlab.be/ns/ql#XPath> ;
		rml:iterator "/locations/place"
	] ;
	rr:subjectMap [
		rr:template "http://sample.com/resource/{name}" ;
		rr:termType rr:IRI ;
		rr:class sch:Place
	] ;
	rr:predicateObjectMap [
		rr:predicate  foaf:name ;
		rr:objectMap [ 
			rml:reference #PLAIN_XML_FIELD ;
			rr:termType rr:Literal ;
		]
	] ;
	rr:predicateObjectMap [
		rr:predicate  onto:surface ;
		rr:objectMap [ 
			rml:reference #NESTED_XMl_FIELD ;
			rr:termType rr:Literal ;
			rr:datatype xsd:double
		]
	] .

solution_template:
@prefix rr: <http://www.w3.org/ns/r2rml#> .
@prefix rml: <http://semweb.mmlab.be/ns/rml#> .
@prefix ql: <http://semweb.mmlab.be/ns/ql#> .
@prefix transit: <http://vocab.org/transit/terms/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix sch: <http://schema.org/> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix onto: <http://vocab.es/schema#> .
@prefix geo: <http://www.w3.org/2003/01/geo/wgs84_pos#> .
@base <http://example.com/ns#> .

<#TriplesMap2> rml:logicalSource [
		rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/nested_locations1.xml" ;
		rml:referenceFormulation <http://semweb.mmlab.be/ns/ql#XPath> ;
		rml:iterator "/locations/place"
	] ;
	rr:subjectMap [
		rr:template "http://sample.com/resource/{name}" ;
		rr:termType rr:IRI ;
		rr:class sch:Place
	] ;
	rr:predicateObjectMap [
		rr:predicate  foaf:name ;
		rr:objectMap [
			rml:reference "name" ;
			rr:termType rr:Literal ;
		]
	] ;
	rr:predicateObjectMap [
		rr:predicate  onto:surface ;
		rr:objectMap [
			rml:reference "features/surface/value" ;
			rr:termType rr:Literal ;
			rr:datatype xsd:double
		]
	] .
