solution_template:
@prefix rr: <http://www.w3.org/ns/r2rml#> .
@prefix rml: <http://semweb.mmlab.be/ns/rml#> .
@prefix ql: <http://semweb.mmlab.be/ns/ql#> .
@prefix transit: <http://vocab.org/transit/terms/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix geo: <http://www.w3.org/2003/01/geo/wgs84_pos#> .
@base <http://example.com/ns#> .

<#TriplesMap2> rml:logicalSource [
			rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/locations.csv" ;
			rml:referenceFormulation ql:CSV
		] ;
		rr:subjectMap [
			rr:template "http://sample.com/resource/{CName}" ;
			rr:termType rr:IRI ;
		] ;
		rr:predicateObjectMap [
			rr:predicate  foaf:name ;
			rr:objectMap [ rml:reference "CName" ; rr:termType rr:Literal ; ]
		] ;
		rr:predicateObjectMap [
			rr:predicate  geo:lat ;
			rr:objectMap [
				rml:reference "latitude" ; rr:termType rr:Literal ;
				rr:datatype xsd:double
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate  geo:long ;
			rr:objectMap [
				rml:reference "longitude" ; rr:termType rr:Literal ;
				rr:datatype xsd:double
			]
		] .
