@prefix rr: <http://www.w3.org/ns/r2rml#> .
@prefix rml: <http://semweb.mmlab.be/ns/rml#> .
@prefix ql: <http://semweb.mmlab.be/ns/ql#> .
@prefix transit: <http://vocab.org/transit/terms/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix sch: <http://schema.org/> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix onto: <http://vocab.es/schema#> .
@prefix geo: <http://www.w3.org/2003/01/geo/wgs84_pos#> .
@base <http://example.com/ns#> .

<#TriplesMap1> rml:logicalSource [
			rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/people.csv" ;
			rml:referenceFormulation <http://semweb.mmlab.be/ns/ql#CSV>
		] ;
		rr:subjectMap [
			rr:termType rr:IRI ;
			rr:template "http://sample.com/resource/P{ID}" ;
			rr:class foaf:Person
		] ;
		rr:predicateObjectMap [
			rr:predicate foaf:name ;
			rr:objectMap [
				rml:reference "Name" ;
				rr:termType rr:Literal ;
				rr:datatype xsd:string
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate foaf:phone ;
			rr:objectMap [
				rml:reference "Phone" ;
				rr:termType rr:Literal
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate rdfs:seeAlso ;
			rr:objectMap [ rr:template "http://dbpedia.org/resource/{City}" ]
		] .

<#TriplesMap2> rml:logicalSource [
			rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/plain_locations.json" ;
			rml:referenceFormulation <http://semweb.mmlab.be/ns/ql#JSONPath> ;
			rml:iterator "$.*"
		] ;
		rr:subjectMap [
			rr:template "http://sample.com/resource/{name}" ;
			rr:termType rr:IRI ;
			rr:class sch:Place
		] ;
		rr:predicateObjectMap [
			rr:predicate sch:name ;
			rr:objectMap [
				rml:reference "name" ;
				rr:termType rr:Literal
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate <http://vocab.es/schema#surface> ;
			rr:objectMap [
				rml:reference "surface" ;
				rr:termType rr:Literal ;
				rr:datatype xsd:double
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate <http://vocab.es/schema#population> ;
			rr:objectMap [
				rml:reference "population" ;
				rr:termType rr:Literal ;
				rr:datatype xsd:double
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate geo:lat ;
			rr:objectMap [
				rml:reference "latitude" ;
				rr:termType rr:Literal ;
				rr:datatype xsd:double
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate geo:long ;
			rr:objectMap [
				rml:reference "longitude" ;
				rr:termType rr:Literal ;
				rr:datatype xsd:double
			]
		] .

