solution_template:
@prefix rr: <http://www.w3.org/ns/r2rml#> .
@prefix rml: <http://semweb.mmlab.be/ns/rml#> .
@prefix ql: <http://semweb.mmlab.be/ns/ql#> .
@prefix transit: <http://vocab.org/transit/terms/> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix foaf: <http://xmlns.com/foaf/0.1/> .
@prefix sch: <http://schema.org/> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix onto: <http://vocab.es/schema#>.
@prefix geo: <http://www.w3.org/2003/01/geo/wgs84_pos#> .
@prefix esbici: <http://vocab.ciudadesabiertas.es/def/transporte/bicicleta-publica#> .
@prefix dct: <http://purl.org/dc/terms/> .
@prefix owl: <http://www.w3.org/2002/07/owl#> .
@prefix own: <http://ourownontology.noexiste/ontology#> .
@base <http://example.com/ns#> .

<#TriplesMap1> rml:logicalSource [
			rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/bikeStations.csv" ;
			rml:referenceFormulation ql:CSV ;
		] ;
		rr:subjectMap [
			rr:template "http://sample.com/resource/EstacionBicicleta/{ID}" ;
			rr:termType rr:IRI ;
			rr:class esbici:EstacionBicicleta
		] ;
		rr:predicateObjectMap [
			rr:predicate dct:identifier ;
			rr:objectMap [
				rml:reference "ID" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:integer
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate own:capacity ;
			rr:objectMap [
				rml:reference "CAPACITY" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:integer
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate own:hasAddress ;
			rr:objectMap [
				rml:reference "STREET" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:string
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate geo:lat ;
			rr:objectMap [
				rml:reference "LATITUDE" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:double
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate geo:long ;
			rr:objectMap [
				rml:reference "LONGITUDE" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:double
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate own:hasNeighbourhood ;
			rr:objectMap [
				rr:parentTriplesMap <#TriplesMap3> ;
				rr:joinCondition [
					rr:child "HOOD_ID" ;
					rr:parent "NEIGHBOURHOOD_ID" ;
				]
			]
		] .

<#TriplesMap2> rml:logicalSource [
			rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/districts.csv" ;
			rml:referenceFormulation ql:CSV ;
		] ;
		rr:subjectMap [
			rr:template "http://sample.com/resource/District/{DISTRICT_ID}" ;
			rr:termType rr:IRI ;
			rr:class own:District
		] ;
		rr:predicateObjectMap [
			rr:predicate dct:identifier ;
			rr:objectMap [
				rml:reference "DISTRICT_ID" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:integer
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate own:name ;
			rr:objectMap [
				rml:reference "DISTRICT_NAME" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:string
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate owl:sameAs ;
			rr:objectMap [
				rml:reference "WIKIDATA_DISTRICT" ;
				rr:termType rr:IRI ;
			]
		] .

<#TriplesMap3> rml:logicalSource [
			rml:source "https://raw.githubusercontent.com/helio-ecosystem/helio-ecosystem/main/resources/mappings-data/ldac/districts.csv" ;
			rml:referenceFormulation ql:CSV ;
		] ;
		rr:subjectMap [
			rr:template "http://sample.com/resource/Neighbourhood/{NEIGHBOURHOOD_ID}" ;
			rr:termType rr:IRI ;
			rr:class own:Neighbourhood
		] ;
		rr:predicateObjectMap [
			rr:predicate dct:identifier ;
			rr:objectMap [
				rml:reference "NEIGHBOURHOOD_ID" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:double
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate own:name ;
			rr:objectMap [
				rml:reference "NEIGHBOURHOOD_NAME" ;
				rr:termType rr:Literal ;
				rr:dataType xsd:string
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate owl:sameAs ;
			rr:objectMap [
				rml:reference "WIKIDATA_NEIGHBOURHOOD" ;
				rr:termType rr:IRI ;
			]
		] ;
		rr:predicateObjectMap [
			rr:predicate own:hasDistrict ;
			rr:objectMap [
				rr:template "http://sample.com/resource/District/{DISTRICT_ID}" ;
			]
		] .
